const dbOperation = (operable) => {
    const db$ = setupOperator(operable);
    return (input$) =>
        combineLatest(input$, db$).pipe(
            mergeMap(([input, db]) => {
                const { operation, collection, params } = input;
                const collectionInstance = db[collection];
                let result;
                switch (operation) {
                    case "findOne":
                    case "find":
                        result = collectionInstance[operation](params).$;
                        break;
                    case "insert":
                    case "upsert":
                    case "patch":
                    case "remove":
                        result = from(collectionInstance[operation](params));
                        break;
                    default:
                        throw new Error(`Unsupported operation: ${operation}`);
                }

                if (!isObservable(result)) {
                    result = of(result);
                }

                return result;
            }),
            map((result) => {
                return result?.toMutableJSON
                    ? result.toMutableJSON()
                    : result?.map((r) => r.toMutableJSON?.() || r);
            })
        );
};

