const getIOTool = (operable, role) =>
    combineLatest(
        operable.io.tools$,
        operable.read.config$.pipe(pluck(role), distinctUntilChanged())
    ).pipe(
        switchMap(([tools, name]) => {
            const tool = tools.find(
                (tool) => tool.id.split("-").pop() === name
            );
            return tool ? of(tool) : EMPTY;
        })
    );

